@app.on_callback_query(filters.regex("^wh_"))
async def open_whisper(_, q: CallbackQuery):
    data = whispers_db.get(q.data.split("_", 1)[1])
    if not data: return await q.answer("Ø§Ù„Ù€Ù‡Ù€Ù…Ù€Ø³Ù€Ù‡ Ù‚Ù€Ø¯ÙŠÙ€Ù…Ù€Ù‡ ğŸ¥€", show_alert=True)
    if q.from_user.id not in [data["from"], data["to"]]: 
        return await q.answer("Ù…Ù€Ø´ Ù„Ù€Ùƒ ÙŠÙ€Ø§ Ø¹Ù€Ù…Ù€à¢ªÙŠ", show_alert=True)
    await q.answer(data["msg"], show_alert=True)

